import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from './vfs_fonts';
import textToBarcode from './textToBarcode'

pdfMake.vfs = pdfFonts.pdfMake.vfs;

const setContent = (documentos) => {
  let paginas = {
    content: '',
    footer: '',
  };
  documentos.forEach((documento, index, array) => {
console.log('<documento>');
console.log(documento);
console.log('</documento>');

const textBlocks = documento.texto.blocks


    const titulo = `${documento.organismo.nombre}\n${
        documento.organismo.circunscripcion
        } Circunscripción Judicial de la Provincia del Neuquén\n${documento.organismo.direccion}, ${
        documento.organismo.ubicacion
        }`;

    const saltoDePagina = {
      text: '',
      pageBreak: 'after',
    };
    //
    let paginaDocumento = [
      {
        // Paso los datos para poder armar un footer dinamico
        text: '  ',
        documento: {
          id: documento.id,
        },
      },
      {
        text: titulo,
        style: 'header',
        margin: [0, -25, 0, 25],
        alignment: 'center',
      },
      documento.marcas.urgente
          ? {
            text: 'URGENTE',
            color: 'red',
            fontSize: 10,
            margin: [0, -25, 0, 25],
            alignment: 'center',
          }
          : null,
      documento.marcas.diaHora
          ? {
            text: 'CON HABILITACION DE DIA Y HORA',
            color: 'red',
            fontSize: 8,
            margin: [0, -25, 0, 25],
            alignment: 'center',
          }
          : null,
      documento.marcas.facultado.nombre
          ? {
            text: 'CON FACULTADO',
            color: 'red',
            fontSize: 8,
            margin: [0, -25, 0, 25],
            alignment: 'center',
          }
          : null,
      {
        text: '',
        margin: [0, 0, 0, 10],
      },
      {
        text: '',
        margin: [0, 0, 0, 25],
      },


      textBlocks.map((linea, index) => {
console.log('<linea>');
console.log(linea);
console.log('</linea>');


        return {
          text: linea,
          style: 'texto',
        };
      }),
      {
        text: '',
        margin: [0, 0, 0, 5],
      },
      documento.marcas.facultado.nombre && {
        text: `Facultado: ${documento.marcas.facultado.nombre},  DNI: ${documento.marcas.facultado.dni}`,
        color: 'red',
        fontSize: 8,
        // margin: [left, top, right, bottom]
        margin: [-50, 0, 0, 0],
        alignment: 'left',
      },
      {
        // Paso los datos para poder armar un footer dinamico
        text: '  ',
        datosFooter: {
          idDocumento: documento.id,
          uid: documento.uid,
          idDocumental: documento.idDocumental,
          tipoID: documento.persona.tipoID,
          numeroID: documento.persona.numeroID,
          sha: documento.sha,
        },
      },
    ];

    if (index + 1 !== array.length) {
      paginaDocumento = [...paginaDocumento, saltoDePagina];
    }

    paginas = {
      content: [...paginas.content, ...paginaDocumento],
    };
  });

  return paginas;
};

const definirDocumento = documentos => {
  pdfMake.fonts = {
    RobotoMono: {
      normal: 'RobotoMono-Regular.ttf',
      bold: 'RobotoMono-Bold.ttf',
      italics: 'RobotoMono-Italic.ttf',
      bolditalics: 'RobotoMono-BoldItalic.ttf',
    },
  };

  const docDefinition = {
    pageSize: 'A4',
    pageMargins: [75, 75, 75, 80],
    defaultStyle: {
      font: 'RobotoMono',
      fontSize: 9,
    },
    styles: {
      header: {
        fontSize: 10,
        bold: true,
        italics: true,
        margin: [0, 0, 0, 10],
      },
      texto: {
        fontSize: 9,
        bold: false,
        textAlign: 'left',
      },
    },
    header: {
      columns: [
        {
          margin: [0, 20, 0, 0],
          text: '',
          width: '80%',
        },
        {
          /* eslint max-len: 0 */
          margin: [0, 20, 0, 0],
          image:
            'data:image/png;base64,',
          width: 45,
        },
      ],
    },
  };


  const paginas = { ...setContent(documentos) };

  docDefinition.content = [...paginas.content];

  docDefinition.footer = function(currentPage, totalPages) {
    let footer = '';

    let paginaDocumento = '';
    let paginaDatos = '';
    let idDocumento = '';
    let idDocumentoDatos = '';

    for (let l = 0; l < docDefinition.content.length; l++) {
      // por defecto entiendo que la pagina es una lista
      let isLista = true;

      // extraigo el documento desde el array de contenido
      const documento = docDefinition.content[l].documento;
      // Puede no existir, ya que se define en el primer elemento del array de la pagina
      if (documento && documento.id) {
        // si existe, no la pagina no es una lista
        isLista = false;
        idDocumento = documento.id;
        paginaDocumento = docDefinition.content[l].positions[0].pageNumber;
      }

      // extraigo los datosFooter desde el array de contenido
      const datosFooter = docDefinition.content[l].datosFooter;

      // Puede no existir, ya que se define en el ultimo elemento del array de la pagina
      if (datosFooter && datosFooter.idDocumento) {
        isLista = false;
        paginaDatos = docDefinition.content[l].positions[0].pageNumber;
        idDocumentoDatos = datosFooter.idDocumento;
      }

      // paginaDatos es la pagina en la que esta el elemento del array
      // si no existe es porque datos footer no existe ergo, la
      // informacion del documento todavia no se completa y el footer
      // tiene que ser minimo.
      // Tambien puede pasar que sea la pagina del listado, en ese
      // caso, el footer tiene que ser vacio

      if (!paginaDatos) {
        if (isLista) {
          footer = [];
        } else {
          footer = [
            {
              canvas: [
                {
                  type: 'line',
                  x1: 0,
                  y1: 5,
                  x2: 250,
                  y2: 5,
                  lineWidth: 0.5,
                },
              ],
            },
            {
              margin: [25, 10],
              alignment: 'center',
              columns: [
                {
                  text: `\n\npagina: ${paginaDocumento}`,
                  alignment: 'left',
                  fontSize: 8,
                  width: '50%',
                },
              ],
            },
          ];
        }
      }

      if (datosFooter && currentPage === paginaDatos) {
        footer = [
          {
            canvas: [
              {
                type: 'line',
                x1: 0,
                y1: 5,
                x2: 250,
                y2: 5,
                lineWidth: 0.5,
              },
            ],
          },
          {
            margin: [25, 10],
            alignment: 'center',
            columns: [
              [
                {
                  text: `Acceda a "https://jusneuquen.gov.ar/validar_documento" e ingrese el código de la derecha para verificar la autenticidad del documento${
                    datosFooter.idDocumental
                      ? ' y descargar la documental.'
                      : '.'
                  }\n
                pagina: ${currentPage - paginaDocumento + 1} de ${paginaDatos -
                    paginaDocumento +
                    1} `,
                  alignment: 'left',
                  fontSize: 8,
                  width: '50%',
                },

                {
                  text: datosFooter.sha,
                  alignment: 'left',
                  fontSize: 6,
                  width: '50%',
                },
              ],

              [
                {
                  image: `${textToBarcode(
                    `${URL}/validar_documento?tipoDocumento=documentos&id=${datosFooter.idDocumento}&uid=${datosFooter.uid}&tipo=${datosFooter.tipoID}&dni=${datosFooter.numeroID}`
                  , 'qrcode')}`,
                  // para usar con: https://github.com/sindresorhus/query-string
                  width: 35,
                  alignment: 'right',
                  // margin: [left, top, right, bottom]
                  margin: [0, 0, 0, 5],
                },
                {
                  text: ` ${datosFooter.uid} `,
                  margin: [0, 0, 5, 0],
                  alignment: 'right',
                  bold: true,
                  fontSize: 9,
                  background: 'black',
                  color: 'white',
                  lineHeight: 1.1,
                },
              ],
            ],
          },
        ];
      }
    }
    return footer;
  };

  return docDefinition;
};

export const print = documentos => {
  const dd = definirDocumento(documentos);

  pdfMake.createPdf(dd).print();
};
